const items = $input.all();
const today = new Date().toISOString().split('T')[0];

let msg = `üìÖ *Daily Summary*\nDate: ${today}\n\n`;

if (items.length === 0) {
  msg += 'No transactions recorded today.';
} else {
  const byEntity = {};
  for (const item of items) {
    const d = item.json;
    if (!byEntity[d.entity]) byEntity[d.entity] = [];
    byEntity[d.entity].push(d);
  }

  for (const [entity, data] of Object.entries(byEntity)) {
    msg += `*${entity}*\n`;
    let total = 0;
    for (const d of data) {
      msg += `  ‚Ä¢ ${d.category}: ‚Ç±${parseInt(d.total).toLocaleString()} (${d.count} txns)\n`;
      total += parseInt(d.total);
    }
    msg += `  Total: ‚Ç±${total.toLocaleString()}\n\n`;
  }
}

return [{ json: { text: msg } }];

const items = $input.all();
const today = new Date();
const weekAgo = new Date(today - 7 * 24 * 60 * 60 * 1000);

let msg = `üìä *Weekly Summary*\n`;
msg += `Period: ${weekAgo.toISOString().split('T')[0]} to ${today.toISOString().split('T')[0]}\n\n`;

if (items.length === 0) {
  msg += 'No transactions recorded this week.';
} else {
  const byEntity = {};
  let grandTotal = 0;

  for (const item of items) {
    const d = item.json;
    if (!byEntity[d.entity]) byEntity[d.entity] = { revenue: 0, expense: 0, count: 0 };
    if (d.category === 'revenue') {
      byEntity[d.entity].revenue += parseInt(d.total);
    } else {
      byEntity[d.entity].expense += parseInt(d.total);
    }
    byEntity[d.entity].count += parseInt(d.count);
  }

  for (const [entity, data] of Object.entries(byEntity)) {
    const net = data.revenue - data.expense;
    const icon = net >= 0 ? '‚úÖ' : '‚ö†Ô∏è';
    msg += `${icon} *${entity}*\n`;
    msg += `   Revenue: ‚Ç±${data.revenue.toLocaleString()}\n`;
    msg += `   Expenses: ‚Ç±${data.expense.toLocaleString()}\n`;
    msg += `   Net: ‚Ç±${net.toLocaleString()}\n`;
    msg += `   Transactions: ${data.count}\n\n`;
    grandTotal += net;
  }

  msg += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
  msg += `*Weekly Net Total: ‚Ç±${grandTotal.toLocaleString()}*`;
}

return [{ json: { text: msg } }];
